import React, { useState, useEffect } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";

const ChatRoom = () => {
  const [client, setClient] = useState(null);
  const [isConnected, setIsConnected] = useState(false);  // ‚úÖ Track connection status
  const [message, setMessage] = useState("");
  const [chatMessages, setChatMessages] = useState([]);

  useEffect(() => {
    const socket = new SockJS("http://localhost:8080/ws");
    const stompClient = new Client({
      webSocketFactory: () => socket,
      reconnectDelay: 5000,   // Auto-reconnect
      onConnect: () => {
        console.log("‚úÖ Connected to WebSocket!");
        setIsConnected(true);  // ‚úÖ Set connection status

        stompClient.subscribe("/topic/public", (msg) => {
          const receivedMessage = JSON.parse(msg.body);
          setChatMessages((prev) => [...prev, receivedMessage]);
        });
      },
      onStompError: (frame) => {
        console.error("‚ùå STOMP Error:", frame.headers["message"]);
        console.error("Details:", frame.body);
        setIsConnected(false);  // ‚ùå Reset connection status on error
      },
      onWebSocketClose: () => {
        console.warn("‚ö†Ô∏è WebSocket closed!");
        setIsConnected(false);  // ‚ùå Reset connection status on disconnect
      },
    });

    stompClient.activate();
    setClient(stompClient);

    return () => {
      if (stompClient) {
        stompClient.deactivate();
      }
    };
  }, []);

  const sendMessage = () => {
    if (client && isConnected && message.trim()) {  // ‚úÖ Check if connected before sending
      const chatMessage = {
        sender: "User",
        content: message,
        timestamp: new Date().toISOString(),
      };

      client.publish({
        destination: "/app/chat.sendMessage",
        body: JSON.stringify(chatMessage),
      });

      setMessage("");
    } else {
      console.warn("‚ö†Ô∏è Cannot send message: STOMP not connected!");
    }
  };

  return (
    <div>
      <h2>Chat Room {isConnected ? "üü¢" : "üî¥"}</h2>
      <div>
        {chatMessages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.sender}: </strong> {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={sendMessage} disabled={!isConnected}>
        {isConnected ? "Send" : "Connecting..."}
      </button>
    </div>
  );
};

export default ChatRoom;
