import React, { useState, useEffect } from "react";
import { Client } from "@stomp/stompjs";

const ChatRoom = () => {
  const [client, setClient] = useState(null);
  const [message, setMessage] = useState("");
  const [chatMessages, setChatMessages] = useState([]);

  useEffect(() => {
    const stompClient = new Client({
      brokerURL: "ws://localhost:8080/ws",  // WebSocket URL
      reconnectDelay: 5000,                 // Auto-reconnect every 5 seconds
    });

    stompClient.onConnect = () => {
      console.log("✅ Connected to WebSocket!");

      stompClient.subscribe("/topic/public", (msg) => {
        const receivedMessage = JSON.parse(msg.body);
        setChatMessages((prev) => [...prev, receivedMessage]);
      });
    };

    stompClient.onStompError = (frame) => {
      console.error("❌ STOMP Error:", frame);
    };

    stompClient.activate();
    setClient(stompClient);

    return () => {
      stompClient.deactivate();
    };
  }, []);

  const sendMessage = () => {
    if (client && client.connected && message.trim()) {
      const chatMessage = {
        sender: "User",  // Replace with dynamic username later
        content: message,
        timestamp: new Date().toISOString()
      };

      client.publish({
        destination: "/app/chat.sendMessage",
        body: JSON.stringify(chatMessage),
      });

      setMessage("");
    }
  };

  return (
    <div>
      <h2>Chat Room {client && client.connected ? "🟢" : "🔴"}</h2>
      <div>
        {chatMessages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.sender}: </strong> {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default ChatRoom;
