import React, { useEffect, useState, useRef } from 'react';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import EmojiPicker from 'emoji-picker-react';
import './ChatRoom.css';

const ChatRoom = ({ username, recipient }) => {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState("");
    const [activeUsers, setActiveUsers] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [darkMode, setDarkMode] = useState(false);
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const [isRecording, setIsRecording] = useState(false);

    const stompClientRef = useRef(null);
    const [isConnected, setIsConnected] = useState(false);
    const latestMessageRef = useRef(null);
    const recognitionRef = useRef(null);

    useEffect(() => {
        const connectWebSocket = () => {
            const socket = new SockJS("http://localhost:8080/chat");
            const client = Stomp.over(socket);

            client.connect({}, () => {
                console.log("Connected to WebSocket");

                const topic = recipient
                    ? `/topic/private/${recipient}`
                    : `/topic/messages`;

                client.subscribe(topic, (msg) => {
                    const receivedMessage = JSON.parse(msg.body);

                    setMessages((prev) => [
                        ...prev,
                        {
                            username: receivedMessage.username,
                            message: receivedMessage.message,
                            timestamp: receivedMessage.timestamp || new Date().toLocaleTimeString(),
                            status: receivedMessage.status
                        }
                    ]);
                });

                stompClientRef.current = client;
                setIsConnected(true);

                const intervalId = setInterval(() => {
                    fetch("http://localhost:8080/active-users")
                        .then((res) => res.json())
                        .then((data) => setActiveUsers(data))
                        .catch((error) => console.error("❌ Failed to fetch active users:", error));
                }, 5000);

                return () => clearInterval(intervalId);
            });
        };

        connectWebSocket();

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.disconnect();
                console.log("❌ Disconnected from WebSocket");
            }
        };
    }, [recipient]);

    useEffect(() => {
        if (latestMessageRef.current) {
            latestMessageRef.current.scrollIntoView({ behavior: "smooth" });
        }
    }, [messages]);

    // 🎤 Speech Recognition Setup
    const toggleVoiceRecording = () => {
        if (!isRecording) {
            if (!window.SpeechRecognition && !window.webkitSpeechRecognition) {
                alert("Your browser does not support speech recognition.");
                return;
            }

            const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
            const recognition = new SpeechRecognition();
            recognition.lang = "en-US";
            recognition.continuous = false;
            recognition.interimResults = false;

            recognition.onstart = () => setIsRecording(true);
            recognition.onresult = (event) => {
                const transcript = event.results[0][0].transcript;
                setMessage(transcript);
            };
            recognition.onerror = (event) => console.error("Speech recognition error:", event.error);
            recognition.onend = () => setIsRecording(false);

            recognition.start();
            recognitionRef.current = recognition;
        } else {
            recognitionRef.current?.stop();
            setIsRecording(false);
        }
    };

    const sendMessage = () => {
        if (stompClientRef.current && stompClientRef.current.connected) {
            const chatMessage = {
                username: username || "Anonymous",
                message: message || "Empty message",
                recipient: recipient || "",
                timestamp: new Date().toLocaleTimeString(),
                status: "Sent"
            };

            stompClientRef.current.send(
                "/app/sendMessage",
                {},
                JSON.stringify(chatMessage)
            );

            setMessage("");
        } else {
            console.error("❌ WebSocket is not connected!");
            alert("Failed to send message. Connection not established.");
        }
    };

    const handleMediaUpload = (event) => {
        const file = event.target.files[0];
        if (!file) return;

        const fileURL = URL.createObjectURL(file);

        setMessages((prev) => [
            ...prev,
            {
                username,
                message: `📎 Sent an attachment`,
                media: fileURL,
                timestamp: new Date().toLocaleTimeString(),
                status: "Sent"
            }
        ]);
    };

    const filteredMessages = messages.filter(
        (msg) =>
            msg.username.toLowerCase().includes(searchQuery.toLowerCase()) ||
            msg.message.toLowerCase().includes(searchQuery.toLowerCase())
    );

    const toggleTheme = () => {
        setDarkMode((prevMode) => !prevMode);
        document.body.classList.toggle('dark-page', !darkMode);
    };

    const handleEmojiClick = (emojiObject) => {
        setMessage((prev) => prev + emojiObject.emoji);
        setShowEmojiPicker(false);
    };

    const getInitials = (name) => name ? name.charAt(0).toUpperCase() : "?";
    
    const getRandomColor = (name) => {
        const colors = ["#FF5733", "#33FF57", "#3357FF", "#FF33A8", "#FF8C33"];
        let index = name ? name.charCodeAt(0) % colors.length : 0;
        return colors[index];
    };

    return (
        <div className={`chat-container ${darkMode ? 'dark-theme' : ''}`}>

            <div className="sidebar">
                <h3>Active Users</h3>
                <ul>
                    {activeUsers.length > 0 ? (
                        activeUsers.map((user, index) => (
                            <li key={index}>{user}</li>
                        ))
                    ) : (
                        <li>No active users</li>
                    )}
                </ul>
            </div>

            <div className="chat-box">
                <div className="chat-header">
                    <h2>{recipient ? `Private Chat with ${recipient}` : "Group Chat"}</h2>
                </div>

                {/* Dark Mode, Start Recording & Media Upload */}
                <div className="top-controls">
                    <button className="theme-toggle-btn" onClick={toggleTheme}>
                        {darkMode ? "☀️ Light Mode" : "🌙 Dark Mode"}
                    </button>

                    <button className="voice-btn" onClick={toggleVoiceRecording}>
                        {isRecording ? "🛑 Stop Recording" : "🎤 Start Recording"}
                    </button>

                    {/* 📎 Upload Media Button */}
                    <label className="media-btn">
                        📎 Upload Media
                        <input type="file" accept="image/*, video/*" onChange={handleMediaUpload} hidden />
                    </label>
                </div>

                <div className="search-bar">
                    <input
                        type="text"
                        placeholder="Search messages..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                    />
                </div>

                <div className="messages">
                    {filteredMessages.map((msg, index) => (
                        <div key={index} ref={index === filteredMessages.length - 1 ? latestMessageRef : null} className={`message ${msg.username === username ? "sent" : "received"}`}>
                            <div className="profile-initials" style={{ backgroundColor: getRandomColor(msg.username) }}>
                                {getInitials(msg.username)}
                            </div>

                            <div className="message-bubble">
                                <p><strong>{msg.username}</strong>: {msg.message}</p>

                                {/* 🖼 Display Uploaded Media */}
                                {msg.media && (
                                    <div className="media-preview">
                                        <img src={msg.media} alt="Uploaded" style={{ maxWidth: "200px", borderRadius: "8px" }} />
                                    </div>
                                )}

                                <small>{msg.timestamp} • {msg.status}</small>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default ChatRoom;
