import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ChatDashboard.css';
import axios from 'axios';

const ChatDashboard = () => {
    const [username, setUsername] = useState('');
    const [activeUsers, setActiveUsers] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch Active Users from Backend
        const fetchActiveUsers = async () => {
            try {
                const response = await axios.get('http://localhost:8080/active-users');
                setActiveUsers([...response.data]);
            } catch (error) {
                console.error('Failed to load active users', error);
            }
        };
        fetchActiveUsers();
    }, []);

    const handleJoinGroup = () => {
        navigate(`/groupchat/${username}`);
    };

    const handleStartPrivateChat = (user) => {
        navigate(`/privatechat/${username}/${user}`);
    };

    return (
        <div className="dashboard-container">
            <h1>ðŸ”¥ Realtime Chat</h1>
            <div className="input-container">
                <input
                    type="text"
                    placeholder="Enter Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <button onClick={handleJoinGroup} disabled={!username}>Join Group Chat</button>
            </div>

            <h2>ðŸ‘¥ Active Users:</h2>
            <div className="user-list">
                {activeUsers.length > 0 ? (
                    activeUsers.map((user, index) => (
                        <div key={index} className="user-card">
                            <span>{user}</span>
                            {user !== username && (
                                <button onClick={() => handleStartPrivateChat(user)}>
                                    ðŸ”¥ Private Chat
                                </button>
                            )}
                        </div>
                    ))
                ) : (
                    <p>No active users</p>
                )}
            </div>
        </div>
    );
};

export default ChatDashboard;
