import React, { useState, useEffect } from 'react';
import { Client } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

function App() {
  const [stompClient, setStompClient] = useState(null);
  const [message, setMessage] = useState('');
  const [chatMessages, setChatMessages] = useState([]);

  useEffect(() => {
    const socket = new SockJS('http://localhost:8080/chat'); // Make sure backend is running at this address
    const client = new Client({
      webSocketFactory: () => socket,
      debug: function (str) {
        console.log(str);
      },
      reconnectDelay: 5000,
      onConnect: () => {
        console.log('Connected');
        client.subscribe('/topic/messages', (msg) => {
          if (msg.body) {
            setChatMessages(prev => [...prev, msg.body]);
          }
        });
      },
      onStompError: (frame) => {
        console.error('Broker error: ' + frame.headers['message']);
        console.error('Additional details: ' + frame.body);
      },
    });

    client.activate();
    setStompClient(client);

    return () => {
      client.deactivate();
    };
  }, []);

  const sendMessage = () => {
    if (stompClient && message.trim() !== '') {
      stompClient.publish({
        destination: '/app/chat', // Spring Boot controller @MessageMapping("/chat")
        body: message,
      });
      setMessage('');
    }
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h1>Real-Time Chat App</h1>
      <div style={{ border: '1px solid #ccc', height: '300px', overflowY: 'auto', padding: '10px', marginBottom: '10px' }}>
        {chatMessages.map((msg, index) => (
          <div key={index} style={{ textAlign: 'left', margin: '5px 0' }}>{msg}</div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Type a message..."
        style={{ width: '70%', padding: '10px', marginRight: '10px' }}
      />
      <button onClick={sendMessage} style={{ padding: '10px 20px' }}>Send</button>
    </div>
  );
}

export default App;
