import React, { useEffect, useState, useRef } from 'react';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import EmojiPicker from 'emoji-picker-react';
import './ChatRoom.css';

const ChatRoom = ({ username, recipient, profilePic }) => {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState("");
    const [activeUsers, setActiveUsers] = useState([]);
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const stompClientRef = useRef(null);
    const [isConnected, setIsConnected] = useState(false);
    const latestMessageRef = useRef(null);

    useEffect(() => {
        const connectWebSocket = () => {
            const socket = new SockJS("http://localhost:8080/chat");
            const client = Stomp.over(socket);

            client.connect({}, () => {
                console.log("âœ… Connected to WebSocket");

                const topic = recipient
                    ? `/topic/private/${recipient}`
                    : `/topic/messages`;

                client.subscribe(topic, (msg) => {
                    const receivedMessage = JSON.parse(msg.body);

                    setMessages((prev) => [
                        ...prev,
                        {
                            username: receivedMessage.username,
                            message: receivedMessage.message,
                            timestamp: receivedMessage.timestamp || new Date().toLocaleTimeString(),
                            status: receivedMessage.status,
                            profilePic: receivedMessage.profilePic // Only for display, not as a message
                        }
                    ]);
                });

                stompClientRef.current = client;
                setIsConnected(true);

                const intervalId = setInterval(() => {
                    fetch("http://localhost:8080/active-users")
                        .then((res) => res.json())
                        .then((data) => setActiveUsers(data))
                        .catch((error) => console.error("âŒ Failed to fetch active users:", error));
                }, 5000);

                return () => clearInterval(intervalId);
            });
        };

        connectWebSocket();

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.disconnect();
                console.log("âŒ Disconnected from WebSocket");
            }
        };
    }, [recipient]);

    useEffect(() => {
        if (latestMessageRef.current) {
            latestMessageRef.current.scrollIntoView({ behavior: "smooth" });
        }
    }, [messages]);

    const sendMessage = () => {
        if (stompClientRef.current && stompClientRef.current.connected) {
            if (!message.trim()) {
                alert("Cannot send an empty message.");
                return;
            }

            const chatMessage = {
                username: username || "Anonymous",
                message: message,  // Ensuring only text is sent
                recipient: recipient || "",
                timestamp: new Date().toLocaleTimeString(),
                status: "Sent",
                profilePic: profilePic // Used only for display, not as a message
            };

            stompClientRef.current.send(
                "/app/sendMessage",
                {},
                JSON.stringify(chatMessage)
            );

            setMessage("");
        } else {
            console.error("âŒ WebSocket is not connected!");
            alert("Failed to send message. Connection not established.");
        }
    };

    const handleEmojiClick = (emojiObject) => {
        setMessage((prev) => prev + emojiObject.emoji);
        setShowEmojiPicker(false);
    };

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Active Users</h3>
                <ul>
                    {activeUsers.length > 0 ? (
                        activeUsers.map((user, index) => (
                            <li key={index}>{user}</li>
                        ))
                    ) : (
                        <li>No active users</li>
                    )}
                </ul>
            </div>

            <div className="chat-box">
                <div className="chat-header">
                    <h2>{recipient ? `Private Chat with ${recipient}` : "Group Chat"}</h2>
                </div>

                <div className="messages">
                    {messages.map((msg, index) => (
                        <div key={index} ref={index === messages.length - 1 ? latestMessageRef : null} className={`message ${msg.username === username ? "sent" : "received"}`}>

                            {/* âœ… Profile Picture or Initials */}
                            <div className="profile-pic">
                                {msg.profilePic ? (
                                    <img src={msg.profilePic} alt="Profile" />
                                ) : (
                                    <div className="profile-initials">
                                        {msg.username.charAt(0).toUpperCase()}
                                    </div>
                                )}
                            </div>

                            {/* âœ… Message Content */}
                            <div className="message-bubble">
                                <p><strong>{msg.username}</strong>: {msg.message}</p>
                                <small>{msg.timestamp} â€¢ {msg.status}</small>
                            </div>
                        </div>
                    ))}
                </div>

                <div className="input-container">
                    <button className="emoji-btn" onClick={() => setShowEmojiPicker((prev) => !prev)}>
                        ðŸ˜Š
                    </button>

                    {showEmojiPicker && (
                        <div className="emoji-picker-container">
                            <EmojiPicker onEmojiClick={handleEmojiClick} />
                        </div>
                    )}

                    <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} placeholder="Type a message..." />

                    <button onClick={sendMessage} disabled={!isConnected}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default ChatRoom;
