import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import MessageList from "./MessageList";
import MessageInput from "./MessageInput";
import UserList from "./UserList";
import "./ChatRoom.css";

const ChatRoom = ({ username }) => {
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);
  const clientRef = useRef(null);
  const [connected, setConnected] = useState(false);  

  // âœ… New: Retry connection logic
  const retryInterval = useRef(null);

  useEffect(() => {
    const connectToWebSocket = () => {
      const socket = new SockJS("http://localhost:8080/ws");
      const client = new Client({
        webSocketFactory: () => socket,
        reconnectDelay: 5000,
        onConnect: () => {
          console.log("âœ… Connected to WebSocket");
          setConnected(true);

          // âœ… Clear retry interval when connected
          if (retryInterval.current) {
            clearInterval(retryInterval.current);
          }

          // âœ… Subscribe to message and user topics
          client.subscribe("/topic/messages", (message) => {
            const newMessage = JSON.parse(message.body);
            setMessages((prev) => [...prev, newMessage]);
          });

          client.subscribe("/topic/users", (userList) => {
            const updatedUsers = JSON.parse(userList.body);
            setUsers(updatedUsers);
          });

          // âœ… Send username on connect
          client.publish({
            destination: "/app/chat.register",
            body: JSON.stringify({ sender: username, content: "JOIN" }),
          });
        },
        onDisconnect: () => {
          console.log("âŒ Disconnected from WebSocket");
          setConnected(false);
        },
        onStompError: (error) => {
          console.error("STOMP error:", error);
        },
      });

      clientRef.current = client;
      client.activate();
    };

    // âœ… Attempt connection on mount
    connectToWebSocket();

    // âœ… Retry connection every 5 seconds if disconnected
    retryInterval.current = setInterval(() => {
      if (!connected) {
        console.log("ðŸ”„ Retrying connection...");
        connectToWebSocket();
      }
    }, 5000);

    return () => {
      if (clientRef.current) {
        clientRef.current.deactivate();
      }
      clearInterval(retryInterval.current);
    };
  }, [username, connected]);

  // âœ… Improved sendMessage function with retry
  const sendMessage = (content) => {
    if (connected && clientRef.current && clientRef.current.connected) {
      clientRef.current.publish({
        destination: "/app/chat.send",
        body: JSON.stringify({ sender: username, content }),
      });
    } else {
      console.warn("âš ï¸ Cannot send message - No STOMP connection. Retrying...");
      setTimeout(() => sendMessage(content), 1000);  // âœ… Retry sending after 1 second
    }
  };

  return (
    <div className="chat-room">
      <UserList users={users} />
      <div className="chat-container">
        <MessageList messages={messages} />
        <MessageInput onSendMessage={sendMessage} />
      </div>
    </div>
  );
};

export default ChatRoom;
