import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ChatDashboard from './ChatDashboard';      // ✅ New Dashboard
import GroupChat from './GroupChat';              // ✅ Group Chat
import PrivateChat from './PrivateChat';          // ✅ Private Chat
import './App.css';

const App = () => {
    return (
        <Router>
            <div className="app-container">
                <Routes>
                    {/* ✅ Chat Selection Screen */}
                    <Route path="/" element={<ChatDashboard />} />

                    {/* ✅ Group Chat */}
                    <Route path="/group/:username" element={<GroupChat />} />

                    {/* ✅ Private Chat */}
                    <Route path="/private/:sender/:recipient" element={<PrivateChat />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
