import React, { useEffect, useState, useRef } from 'react';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import EmojiPicker from 'emoji-picker-react';
import './ChatRoom.css';

const ChatRoom = ({ username, recipient }) => {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState("");
    const [activeUsers, setActiveUsers] = useState([]);
    const [profilePic, setProfilePic] = useState(null);
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);

    const stompClientRef = useRef(null);
    const [isConnected, setIsConnected] = useState(false);
    const latestMessageRef = useRef(null);

    useEffect(() => {
        const connectWebSocket = () => {
            const socket = new SockJS("http://localhost:8080/chat");
            const client = Stomp.over(socket);

            client.connect({}, () => {
                console.log("‚úÖ Connected to WebSocket");

                const topic = recipient ? `/topic/private/${recipient}` : `/topic/messages`;

                client.subscribe(topic, (msg) => {
                    const receivedMessage = JSON.parse(msg.body);

                    setMessages((prev) => [
                        ...prev,
                        {
                            username: receivedMessage.username,
                            message: receivedMessage.message,
                            timestamp: receivedMessage.timestamp || new Date().toLocaleTimeString(),
                            profilePic: receivedMessage.profilePic || null
                        }
                    ]);
                });

                stompClientRef.current = client;
                setIsConnected(true);
            });
        };

        connectWebSocket();

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.disconnect();
                console.log("‚ùå Disconnected from WebSocket");
            }
        };
    }, [recipient]);

    useEffect(() => {
        if (latestMessageRef.current) {
            latestMessageRef.current.scrollIntoView({ behavior: "smooth" });
        }
    }, [messages]);

    // üõ†Ô∏è FIX: Resize Image Before Sending
    const handleProfilePicUpload = (event) => {
        const file = event.target.files[0];
        if (!file) return;

        const reader = new FileReader();
        reader.onload = (e) => {
            const img = new Image();
            img.src = e.target.result;
            img.onload = () => {
                const canvas = document.createElement("canvas");
                const ctx = canvas.getContext("2d");

                // Resize Image to 100x100 to reduce WebSocket message size
                canvas.width = 100;
                canvas.height = 100;
                ctx.drawImage(img, 0, 0, 100, 100);

                // Convert to Base64
                const resizedImage = canvas.toDataURL("image/jpeg", 0.7);
                setProfilePic(resizedImage);
            };
        };
        reader.readAsDataURL(file);
    };

    const sendMessage = () => {
        if (!stompClientRef.current || !stompClientRef.current.connected) {
            console.error("‚ùå WebSocket is not connected!");
            alert("Failed to send message. Connection not established.");
            return;
        }

        const chatMessage = {
            username: username || "Anonymous",
            message: message.trim() || "Empty message",
            recipient: recipient || "",
            timestamp: new Date().toLocaleTimeString(),
            profilePic: profilePic || null,  // Now sending profile picture safely
        };

        stompClientRef.current.send("/app/sendMessage", {}, JSON.stringify(chatMessage));
        setMessage("");
    };

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Active Users</h3>
                <ul>
                    {activeUsers.length > 0 ? (
                        activeUsers.map((user, index) => (
                            <li key={index}>{user}</li>
                        ))
                    ) : (
                        <li>No active users</li>
                    )}
                </ul>
            </div>

            <div className="chat-box">
                <div className="chat-header">
                    <h2>{recipient ? `Private Chat with ${recipient}` : "Group Chat"}</h2>
                    <label className="profile-upload">
                        üì∏ Upload Profile Picture
                        <input type="file" accept="image/*" onChange={handleProfilePicUpload} hidden />
                    </label>
                </div>

                <div className="messages">
                    {messages.map((msg, index) => (
                        <div key={index} ref={index === messages.length - 1 ? latestMessageRef : null} className={`message ${msg.username === username ? "sent" : "received"}`}>
                            <div className="profile-pic">
                                {msg.profilePic ? (
                                    <img src={msg.profilePic} alt="Profile" />
                                ) : (
                                    <div className="profile-initials">
                                        {msg.username.charAt(0).toUpperCase()}
                                    </div>
                                )}
                            </div>
                            <div className="message-bubble">
                                <p><strong>{msg.username}</strong>: {msg.message}</p>
                                <small>{msg.timestamp}</small>
                            </div>
                        </div>
                    ))}
                </div>

                <div className="input-container">
                    <button className="emoji-btn" onClick={() => setShowEmojiPicker((prev) => !prev)}>
                        üòä
                    </button>

                    {showEmojiPicker && (
                        <div className="emoji-picker-container">
                            <EmojiPicker onEmojiClick={(emoji) => setMessage((prev) => prev + emoji.emoji)} />
                        </div>
                    )}

                    <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} placeholder="Type a message..." />
                    <button onClick={sendMessage} disabled={!isConnected}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default ChatRoom;
