import React, { useEffect, useState } from 'react';
import SockJS from 'sockjs-client';
import { Client } from '@stomp/stompjs';

export default function ChatRoom({ user }) {
  const [client, setClient] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const socket = new SockJS('http://localhost:8080/ws');
    const stompClient = new Client({ webSocketFactory: () => socket });

    stompClient.onConnect = () => {
      stompClient.subscribe('/topic/messages', (msg) => setMessages((prev) => [...prev, JSON.parse(msg.body)]));
    };
    stompClient.activate();
    setClient(stompClient);
    return () => stompClient.deactivate();
  }, []);

  const sendMessage = () => {
    if (message.trim()) {
      client.publish({ destination: '/app/chat', body: JSON.stringify({ content: message }) });
      setMessage('');
    }
  };

  return (
    <div>
      <h2>Welcome, {user}</h2>
      <div className="chat-box">
        {messages.map((msg, idx) => (
          <div key={idx} className="message">
            <strong>{msg.sender}:</strong> {msg.content}
          </div>
        ))}
      </div>
      <input value={message} onChange={(e) => setMessage(e.target.value)} placeholder="Type a message..." />
      <button onClick={sendMessage} className="send">Send</button>
    </div>
  );
}
