import React, { useEffect, useState, useRef } from 'react';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';

const ChatRoom = ({ username }) => {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState("");
    
    const stompClientRef = useRef(null);   // Use ref to avoid re-initializing WebSocket
    const [isConnected, setIsConnected] = useState(false);   // Track connection status

    useEffect(() => {
        const socket = new SockJS("http://localhost:8080/chat");
        const client = Stomp.over(socket);

        client.connect({}, () => {
            console.log("Connected to WebSocket!");
            setIsConnected(true);   // Mark connection as established

            client.subscribe("/topic/messages", (msg) => {
                const receivedMessage = JSON.parse(msg.body);
                setMessages((prev) => [...prev, receivedMessage]);
            });

            stompClientRef.current = client;  // Store client in ref
        });

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.disconnect();
            }
        };
    }, []);

    const sendMessage = () => {
        if (isConnected && stompClientRef.current) {   // Check connection status
            const chatMessage = {
                username: username,
                message: message
            };
            stompClientRef.current.send("/app/sendMessage", {}, JSON.stringify(chatMessage));
            setMessage("");
        } else {
            console.error("WebSocket not connected!");
        }
    };

    return (
        <div>
            <h2>Chat Room</h2>
            <div>
                {messages.map((msg, index) => (
                    <p key={index}><b>{msg.username}:</b> {msg.message}</p>
                ))}
            </div>
            <input 
                type="text" 
                value={message} 
                onChange={(e) => setMessage(e.target.value)} 
                placeholder="Type a message..."
            />
            <button onClick={sendMessage} disabled={!isConnected}>
                Send
            </button>
            {!isConnected && <p>Connecting...</p>}
        </div>
    );
};

export default ChatRoom;
