import React from 'react';
import { useNavigate } from 'react-router-dom';
import './ChatDashboard.css';  // âœ… Add CSS for better styling

const ChatDashboard = () => {
    const navigate = useNavigate();

    // âœ… Navigate to Group Chat
    const goToGroupChat = () => {
        const username = prompt("Enter your username:");
        if (username) {
            navigate(`/group/${username}`);
        }
    };

    // âœ… Navigate to Private Chat
    const goToPrivateChat = () => {
        const sender = prompt("Enter your username:");
        const recipient = prompt("Enter recipient's username:");
        if (sender && recipient) {
            navigate(`/private/${sender}/${recipient}`);
        }
    };

    return (
        <div className="dashboard-container">
            <h1>ðŸ”¥ Real-Time Chat App</h1>
            <div className="button-container">
                <button onClick={goToGroupChat} className="btn group-btn">Group Chat</button>
                <button onClick={goToPrivateChat} className="btn private-btn">Private Chat</button>
            </div>
        </div>
    );
};

export default ChatDashboard;
