import React, { useEffect, useState } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { useNavigate } from "react-router-dom";
import "./ChatDashboard.css";  // Add CSS for layout

const ChatDashboard = ({ username }) => {
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  const client = new Client({
    webSocketFactory: () => new SockJS("http://localhost:8080/chat"),
  });

  useEffect(() => {
    client.onConnect = () => {
      console.log("Connected to WebSocket");

      // Subscribe to the active users list
      client.subscribe("/topic/users", (message) => {
        const userList = JSON.parse(message.body);
        setUsers(userList);
      });

      // Notify server of new user
      client.publish({
        destination: "/app/join",
        body: username,
      });
    };

    client.activate();
    return () => client.deactivate();
  }, [username]);

  const openPrivateChat = (recipient) => {
    window.open(`/private/${recipient}`, "_blank");  // Opens chat in a new tab
  };

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <h3>Active Users:</h3>
        <ul>
          {users.map((user, index) => (
            <li key={index} className="user-item">
              <span>{user}</span>
              {user !== username && (
                <button
                  className="private-btn"
                  onClick={() => openPrivateChat(user)}
                >
                  Private Chat
                </button>
              )}
            </li>
          ))}
        </ul>
      </div>

      <div className="chat-options">
        <h2>Chat Options</h2>
        <button onClick={() => navigate("/group")}>Join Group Chat</button>
      </div>
    </div>
  );
};

export default ChatDashboard;
