import React, { useEffect, useState } from 'react';
import { connectWebSocket, sendMessage } from './WebSocketService';

const App = () => {
  const [username, setUsername] = useState(''); // To store username
  const [isRegistered, setIsRegistered] = useState(false); // To check if user has entered username
  const [messages, setMessages] = useState([]); // Chat messages
  const [inputMessage, setInputMessage] = useState(''); // Current input

  // Connect WebSocket and handle incoming messages
  useEffect(() => {
    connectWebSocket((message) => {
      setMessages((prev) => [...prev, message]); // Add incoming message to chat
    });
  }, []);

  // Handle sending message
  const handleSendMessage = () => {
    if (inputMessage.trim() !== '' && username) {
      sendMessage({ sender: username, content: inputMessage }); // Send with username
      setInputMessage(''); // Clear input field
    }
  };

  // Handle registration (username input)
  const handleRegister = () => {
    if (username.trim() !== '') {
      setIsRegistered(true); // Mark user as registered
    }
  };

  // Render username input if not registered
  if (!isRegistered) {
    return (
      <div style={{ padding: '20px', textAlign: 'center' }}>
        <h2>Enter Your Name to Join Chat</h2>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Your name"
          style={{ padding: '10px', width: '60%' }}
        />
        <button onClick={handleRegister} style={{ padding: '10px 20px', marginLeft: '10px' }}>
          Join
        </button>
      </div>
    );
  }

  // Render chat screen
  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <h1>Welcome, {username}!</h1>

      {/* Chat messages display */}
      <div style={{ border: '1px solid black', padding: '10px', minHeight: '300px', overflowY: 'auto' }}>
        {messages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.sender}:</strong> {msg.content}
          </div>
        ))}
      </div>

      {/* Message input */}
      <div style={{ marginTop: '10px' }}>
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          placeholder="Type your message..."
          style={{ width: '80%', padding: '10px' }}
        />
        <button onClick={handleSendMessage} style={{ padding: '10px 20px' }}>
          Send
        </button>
      </div>
    </div>
  );
};

export default App;
