import React, { useState } from 'react';  // ✅ Removed unused imports
import './ChatRoom.css';

const ChatRoom = ({ username, recipient }) => {
    const [searchQuery, setSearchQuery] = useState('');
    const [filteredMessages, setFilteredMessages] = useState([]);
    const [editMessageId, setEditMessageId] = useState(null);
    const [editedText, setEditedText] = useState("");

    const searchMessages = async () => {
        const response = await fetch(`http://localhost:8080/search?query=${searchQuery}`);
        const data = await response.json();
        setFilteredMessages(data);
    };

    const editMessage = async (id, newText) => {
        await fetch(`http://localhost:8080/edit/${id}?newMessage=${newText}`, {
            method: 'PUT'
        });
        setEditMessageId(null);
        setEditedText("");
    };

    return (
        <div className="chat-container">
            <input
                type="text"
                placeholder="Search messages..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button onClick={searchMessages}>Search</button>

            <div className="messages">
                {filteredMessages.map((msg) => (
                    <div key={msg.id} className="message-bubble">
                        <p><strong>{msg.username}</strong>: {msg.message} {msg.edited && "(edited)"}</p>
                        <button onClick={() => setEditMessageId(msg.id)}>✏️ Edit</button>
                        {editMessageId === msg.id && (
                            <>
                                <input
                                    type="text"
                                    value={editedText}
                                    onChange={(e) => setEditedText(e.target.value)}
                                />
                                <button onClick={() => editMessage(msg.id, editedText)}>Save</button>
                            </>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ChatRoom;
