import React, { useState, useEffect } from "react";
import { Client } from '@stomp/stompjs';

const ChatRoom = () => {
  const [stompClient, setStompClient] = useState(null);
  const [message, setMessage] = useState("");
  const [chatMessages, setChatMessages] = useState([]);
  const [isConnected, setIsConnected] = useState(false);  // âœ… Added connection state

  useEffect(() => {
    const client = new Client({
      brokerURL: "ws://localhost:8080/ws",
      reconnectDelay: 5000,
      heartbeatIncoming: 4000,
      heartbeatOutgoing: 4000,
    });

    client.onConnect = () => {
      console.log("Connected to WebSocket âœ…");
      setIsConnected(true);  // âœ… Mark as connected

      client.subscribe("/topic/public", (message) => {
        const msg = JSON.parse(message.body);
        setChatMessages((prev) => [...prev, msg]);
      });

      setStompClient(client);
    };

    client.onStompError = (frame) => {
      console.error("Broker reported error: ", frame.headers["message"]);
      console.error("Additional details: ", frame.body);
      setIsConnected(false);  // âŒ Mark as disconnected
    };

    client.activate();

    return () => {
      if (client) {
        client.deactivate();
        setIsConnected(false);
      }
    };
  }, []);

  const sendMessage = () => {
    if (!stompClient || !isConnected) {
      console.warn("WebSocket not connected, cannot send message!");
      return;
    }

    const chatMessage = {
      sender: "User",  // Replace with dynamic username later
      content: message,
      timestamp: new Date().toISOString(),
    };

    stompClient.publish({
      destination: "/app/chat.sendMessage",
      body: JSON.stringify(chatMessage),
    });

    setMessage("");
  };

  return (
    <div>
      <h2>Chat Room {isConnected ? "ðŸŸ¢" : "ðŸ”´"}</h2>
      <div>
        {chatMessages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.sender}:</strong> {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={sendMessage} disabled={!isConnected}>
        Send
      </button>
    </div>
  );
};

export default ChatRoom;
