import React, { useEffect, useState, useRef } from 'react';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import EmojiPicker from 'emoji-picker-react';
import './ChatRoom.css';

const ChatRoom = ({ username, recipient }) => {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState("");
    const [activeUsers, setActiveUsers] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [darkMode, setDarkMode] = useState(false);
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const [isRecording, setIsRecording] = useState(false);
    const [mediaRecorder, setMediaRecorder] = useState(null);

    const stompClientRef = useRef(null);
    const [isConnected, setIsConnected] = useState(false);

    const latestMessageRef = useRef(null);

    useEffect(() => {
        const connectWebSocket = () => {
            const socket = new SockJS("http://localhost:8080/chat");
            const client = Stomp.over(socket);

            client.connect({}, () => {
                console.log("Connected to WebSocket");

                const topic = recipient
                    ? `/topic/private/${recipient}`
                    : `/topic/messages`;

                client.subscribe(topic, (msg) => {
                    const receivedMessage = JSON.parse(msg.body);

                    setMessages((prev) => [
                        ...prev,
                        {
                            username: receivedMessage.username,
                            message: receivedMessage.message,
                            audio: receivedMessage.audio, // Audio message support
                            timestamp: receivedMessage.timestamp || new Date().toLocaleTimeString(),
                            status: receivedMessage.status
                        }
                    ]);
                });

                stompClientRef.current = client;
                setIsConnected(true);

                const intervalId = setInterval(() => {
                    fetch("http://localhost:8080/active-users")
                        .then((res) => res.json())
                        .then((data) => setActiveUsers(data))
                        .catch((error) => console.error("‚ùå Failed to fetch active users:", error));
                }, 5000);

                return () => clearInterval(intervalId);
            });
        };

        connectWebSocket();

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.disconnect();
                console.log("‚ùå Disconnected from WebSocket");
            }
        };
    }, [recipient]);

    useEffect(() => {
        if (latestMessageRef.current) {
            latestMessageRef.current.scrollIntoView({ behavior: "smooth" });
        }
    }, [messages]);

    const sendMessage = () => {
        if (stompClientRef.current && stompClientRef.current.connected) {
            const chatMessage = {
                username: username || "Anonymous",
                message: message || "Empty message",
                recipient: recipient || "",
                timestamp: new Date().toLocaleTimeString(),
                status: "Sent"
            };

            stompClientRef.current.send(
                "/app/sendMessage",
                {},
                JSON.stringify(chatMessage)
            );

            setMessage("");
        } else {
            console.error("‚ùå WebSocket is not connected!");
            alert("Failed to send message. Connection not established.");
        }
    };

    const toggleTheme = () => {
        setDarkMode((prevMode) => !prevMode);
        document.body.classList.toggle('dark-page', !darkMode);
    };

    const handleEmojiClick = (emojiObject) => {
        setMessage((prev) => prev + emojiObject.emoji);
        setShowEmojiPicker(false);
    };

    // Voice Recording Functions
    const startRecording = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            const recorder = new MediaRecorder(stream);
            let audioChunks = [];

            recorder.ondataavailable = (event) => {
                if (event.data.size > 0) {
                    audioChunks.push(event.data);
                }
            };

            recorder.onstop = async () => {
                const audioBlob = new Blob(audioChunks, { type: "audio/webm" });
                const reader = new FileReader();

                reader.readAsDataURL(audioBlob);
                reader.onloadend = () => {
                    sendVoiceMessage(reader.result);
                };
            };

            recorder.start();
            setMediaRecorder(recorder);
            setIsRecording(true);
        } catch (error) {
            console.error("‚ùå Error starting recording:", error);
            alert("Microphone access denied!");
        }
    };

    const stopRecording = () => {
        if (mediaRecorder) {
            mediaRecorder.stop();
            setIsRecording(false);
        }
    };

    const sendVoiceMessage = (audioData) => {
        if (stompClientRef.current && stompClientRef.current.connected) {
            const voiceMessage = {
                username: username || "Anonymous",
                message: "", // No text, only voice
                recipient: recipient || "",
                audio: audioData, // Base64 audio
                timestamp: new Date().toLocaleTimeString(),
                status: "Sent"
            };

            stompClientRef.current.send(
                "/app/sendMessage",
                {},
                JSON.stringify(voiceMessage)
            );
        } else {
            console.error("‚ùå WebSocket is not connected!");
            alert("Failed to send voice message. Connection not established.");
        }
    };

    return (
        <div className={`chat-container ${darkMode ? 'dark-theme' : ''}`}>

            <div className="sidebar">
                <h3>Active Users</h3>
                <ul>
                    {activeUsers.length > 0 ? (
                        activeUsers.map((user, index) => (
                            <li key={index}>{user}</li>
                        ))
                    ) : (
                        <li>No active users</li>
                    )}
                </ul>
            </div>

            <div className="chat-box">
                <div className="chat-header">
                    <h2>{recipient ? `Private Chat with ${recipient}` : "Group Chat"}</h2>

                    {/* Dark mode toggle button */}
                    <button className="theme-toggle-btn" onClick={toggleTheme}>
                        {darkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
                    </button>
                </div>

                <div className="search-bar">
                    <input
                        type="text"
                        placeholder="Search messages..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                    />
                </div>

                <div className="messages">
                    {messages.length > 0 ? (
                        messages.map((msg, index) => (
                            <div key={index} className={`message ${msg.username === username ? "sent" : "received"}`}>
                                <div className="message-bubble">
                                    <p><strong>{msg.username}</strong>:</p>
                                    {msg.audio ? (
                                        <audio controls>
                                            <source src={msg.audio} type="audio/webm" />
                                            Your browser does not support the audio element.
                                        </audio>
                                    ) : (
                                        <p>{msg.message}</p>
                                    )}
                                    <small>{msg.timestamp} ‚Ä¢ {msg.status}</small>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>No messages found...</p>
                    )}
                </div>

                <div className="input-container">
                    <button className="emoji-btn" onClick={() => setShowEmojiPicker((prev) => !prev)}>üòä</button>
                    {showEmojiPicker && <EmojiPicker onEmojiClick={handleEmojiClick} />}

                    <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} placeholder="Type a message..." />
                    <button onClick={sendMessage} disabled={!isConnected}>Send</button>

                    {/* Voice Recording Button */}
                    <button className="voice-btn" onClick={isRecording ? stopRecording : startRecording}>
                        {isRecording ? "‚èπÔ∏è Stop" : "üé§ Record"}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ChatRoom;
