import React, { useEffect, useState } from 'react';
import { Client } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

const ChatRoom = ({ username }) => {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [isTyping, setIsTyping] = useState('');
  const client = new Client();

  useEffect(() => {
    client.webSocketFactory = () => new SockJS('http://localhost:8080/chat');

    client.onConnect = () => {
      console.log('Connected to WebSocket');

      // Subscribe to messages
      client.subscribe('/topic/messages', (msg) => {
        const receivedMessage = JSON.parse(msg.body);
        setMessages((prev) => [...prev, receivedMessage]);
      });

      // Subscribe to active users
      client.subscribe('/topic/users', (userList) => {
        setUsers(JSON.parse(userList.body));
      });

      // Subscribe to typing indicator
      client.subscribe('/topic/typing', (status) => {
        setIsTyping(status.body);
        setTimeout(() => setIsTyping(''), 2000);
      });

      // Send username to join
      client.publish({
        destination: '/app/join',
        body: username
      });
    };

    client.activate();

    return () => {
      client.deactivate();
    };
  }, [username]);

  const sendMessage = () => {
    if (message.trim()) {
      client.publish({
        destination: '/app/send',
        body: JSON.stringify({ username, content: message })
      });
      setMessage('');
    }
  };

  const handleTyping = () => {
    client.publish({
      destination: '/app/typing',
      body: username
    });
  };

  return (
    <div className="chat-container">
      <h2>Group Chat</h2>

      {/* Active Users List */}
      <div className="user-list">
        <h3>Active Users</h3>
        {users.map((user, idx) => (
          <div key={idx}>{user}</div>
        ))}
      </div>

      {/* Messages */}
      <div className="messages">
        {messages.map((msg, idx) => (
          <div key={idx} className="message">
            <strong>{msg.username}</strong>: {msg.content}
            <div className="timestamp">{new Date(msg.timestamp).toLocaleString()}</div>
          </div>
        ))}
      </div>

      {/* Typing Indicator */}
      {isTyping && <p className="typing">{isTyping}</p>}

      {/* Input Section */}
      <div className="input-section">
        <input
          type="text"
          placeholder="Type a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={handleTyping}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatRoom;
