import React, { useEffect, useState } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";

const GroupChat = ({ username }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [client, setClient] = useState(null);  
  const [isConnected, setIsConnected] = useState(false);  

  useEffect(() => {
    const stompClient = new Client({
      webSocketFactory: () => new SockJS("http://localhost:8080/chat"),
      onConnect: () => {
        console.log("Connected to Group Chat");
        setIsConnected(true);

        stompClient.subscribe("/topic/group", (message) => {
          const msg = JSON.parse(message.body);
          setMessages((prev) => [...prev, msg]);
        });
      },
      onDisconnect: () => {
        console.log("Disconnected");
        setIsConnected(false);
      },
    });

    stompClient.activate();
    setClient(stompClient);

    return () => stompClient.deactivate();
  }, []);

  const sendMessage = () => {
    if (isConnected && client) {
      client.publish({
        destination: "/app/send",
        body: JSON.stringify({ username, content: input }),
      });
      setInput("");
    } else {
      console.error("STOMP connection not established yet.");
      alert("WebSocket connection not established. Please wait.");
    }
  };

  return (
    <div>
      <h2>Group Chat</h2>
      <div>
        {messages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.username}:</strong> {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default GroupChat;
