import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Client } from '@stomp/stompjs';
import './PrivateChat.css';

const PrivateChat = () => {
    const { username, recipient } = useParams();
    const [client, setClient] = useState(null);
    const [connected, setConnected] = useState(false);
    const [messages, setMessages] = useState([]);   // ‚úÖ Initialize as empty array
    const [message, setMessage] = useState('');

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await fetch(`http://localhost:8080/api/chat/private/${username}/${recipient}`, {
                    mode: 'cors'
                });

                if (!response.ok) {
                    throw new Error(`Failed to fetch messages: ${response.status}`);
                }

                const data = await response.json();
                console.log("Fetched messages:", data);

                // ‚úÖ Ensure messages is always an array
                setMessages(Array.isArray(data) ? data : []);  

            } catch (error) {
                console.error('Error fetching private messages:', error);
                setMessages([]);   // ‚úÖ Fallback to empty array on error
            }
        };

        fetchMessages();

        const newClient = new Client({
            brokerURL: 'ws://localhost:8080/ws',
            reconnectDelay: 5000, 
            onConnect: () => {
                console.log('üî• Connected to WebSocket');
                setConnected(true);

                // ‚úÖ Subscribe to private chat between the two users
                newClient.subscribe(`/user/${username}/queue/messages`, (message) => {
                    const newMessage = JSON.parse(message.body);
                    setMessages((prev) => [...prev, newMessage]);
                });
            },
            onDisconnect: () => {
                console.log('‚ùå Disconnected from WebSocket');
                setConnected(false);
            },
            onStompError: (frame) => {
                console.error('‚ùå STOMP Error:', frame);
            }
        });

        newClient.activate();
        setClient(newClient);

        return () => {
            newClient.deactivate();
        };
    }, [username, recipient]);

    const sendMessage = () => {
        if (!client || !connected) {
            console.error('‚ùå WebSocket is not connected.');
            alert('WebSocket connection is not active. Please wait...');
            return;
        }

        if (message.trim()) {
            const msg = {
                sender: username,
                recipient: recipient,
                content: message
            };

            client.publish({
                destination: '/app/chat.private',
                body: JSON.stringify(msg)
            });

            setMessage('');
        }
    };

    return (
        <div className="private-chat-container">
            <h2>üî• Private Chat with {recipient}</h2>

            <div className="chat-box">
                {Array.isArray(messages) && messages.length > 0 ? (
                    messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.sender === username ? 'sent' : 'received'}`}>
                            <strong>{msg.sender}:</strong> {msg.content}
                        </div>
                    ))
                ) : (
                    <p>No messages yet. Start a conversation!</p>
                )}
            </div>

            <div className="chat-input">
                <input
                    type="text"
                    placeholder="Type a message..."
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                />
                <button onClick={sendMessage} disabled={!connected}>Send</button>
            </div>
        </div>
    );
};

export default PrivateChat;
