import React, { useState, useEffect } from "react";
import { Client } from "@stomp/stompjs";

const ChatRoom = () => {
  const [client, setClient] = useState(null);
  const [message, setMessage] = useState("");
  const [chatMessages, setChatMessages] = useState([]);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    const stompClient = new Client({
      brokerURL: "ws://localhost:8080/ws",
      reconnectDelay: 5000,
      heartbeatIncoming: 4000,
      heartbeatOutgoing: 4000,
      onConnect: () => {
        console.log("Connected âœ…");
        setConnected(true);

        stompClient.subscribe("/topic/public", (msg) => {
          if (msg.body) {
            const receivedMessage = JSON.parse(msg.body);
            
            // âœ… Display message correctly
            setChatMessages((prevMessages) => [...prevMessages, receivedMessage]);
          }
        });
      },
      onDisconnect: () => {
        console.log("Disconnected âŒ");
        setConnected(false);
      },
      onStompError: (frame) => {
        console.error("Broker reported error: " + frame.headers["message"]);
        console.error("Additional details: " + frame.body);
      },
    });

    stompClient.activate();
    setClient(stompClient);

    return () => {
      if (stompClient) {
        stompClient.deactivate();
      }
    };
  }, []);

  const sendMessage = () => {
    if (client && message.trim() !== "") {
      const chatMessage = {
        sender: "User",  // âœ… Replace with dynamic username later
        content: message,
        timestamp: new Date().toISOString()
      };

      client.publish({
        destination: "/app/chat.sendMessage",
        body: JSON.stringify(chatMessage),
      });

      // âœ… Display sent message in the chat window
      setChatMessages((prev) => [...prev, chatMessage]);
      setMessage("");
    }
  };

  return (
    <div>
      <h2>Chat Room {connected ? "ðŸŸ¢" : "ðŸ”´"}</h2>
      <div style={{ height: "400px", overflowY: "auto", border: "1px solid #ccc", padding: "10px" }}>
        {chatMessages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.sender}: </strong> {msg.content}
            <br />
            <small>{new Date(msg.timestamp).toLocaleTimeString()}</small>
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default ChatRoom;
