import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Client } from '@stomp/stompjs';
import './GroupChat.css';

const GroupChat = () => {
    const { username } = useParams();
    const [client, setClient] = useState(null);
    const [connected, setConnected] = useState(false);
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');

    useEffect(() => {
        // ‚úÖ Fetch Previous Group Messages from the Database
        const fetchMessages = async () => {
            try {
                const response = await fetch('http://localhost:8080/api/chat/group');
                const data = await response.json();
                setMessages(data);
            } catch (error) {
                console.error('Error fetching group messages:', error);
            }
        };

        fetchMessages();

        // ‚úÖ Initialize STOMP Client
        const newClient = new Client({
            brokerURL: 'ws://localhost:8080/ws',
            reconnectDelay: 5000, // ‚úÖ Auto-reconnect on failure
            debug: (msg) => console.log(msg),
            onConnect: () => {
                console.log('üî• Connected to WebSocket');
                setConnected(true);

                // ‚úÖ Subscribe to Group Chat
                newClient.subscribe('/topic/public', (message) => {
                    const newMessage = JSON.parse(message.body);
                    setMessages((prev) => [...prev, newMessage]);
                });
            },
            onDisconnect: () => {
                console.log('‚ùå Disconnected from WebSocket');
                setConnected(false);
            },
            onStompError: (frame) => {
                console.error('‚ùå STOMP Error:', frame);
            },
        });

        newClient.activate();
        setClient(newClient);

        return () => {
            newClient.deactivate();
        };
    }, []);

    // ‚úÖ Send Group Message with Connection Check
    const sendMessage = () => {
        if (!client || !connected) {
            console.error('‚ùå WebSocket is not connected.');
            alert('WebSocket connection is not active. Please wait...');
            return;
        }

        if (message.trim()) {
            const msg = {
                sender: username,
                content: message,
            };

            client.publish({
                destination: '/app/chat.send',
                body: JSON.stringify(msg),
            });

            setMessage('');
        }
    };

    return (
        <div className="group-chat-container">
            <h2>üî• Group Chat</h2>
            <div className="chat-box">
                {messages.map((msg, index) => (
                    <div key={index} className={`message ${msg.sender === username ? 'sent' : 'received'}`}>
                        <strong>{msg.sender}:</strong> {msg.content}
                    </div>
                ))}
            </div>
            <div className="chat-input">
                <input
                    type="text"
                    placeholder="Type a message..."
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                />
                <button onClick={sendMessage} disabled={!connected}>Send</button>
            </div>
        </div>
    );
};

export default GroupChat;
