import React, { useEffect, useState, useRef } from 'react';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import './ChatRoom.css';

const ChatRoom = ({ username, recipient }) => {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState("");
    const [activeUsers, setActiveUsers] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [darkMode, setDarkMode] = useState(false);
    const [isTyping, setIsTyping] = useState(false);
    const [typingUser, setTypingUser] = useState("");

    const stompClientRef = useRef(null);
    const typingTimeoutRef = useRef(null);

    useEffect(() => {
        const connectWebSocket = () => {
            const socket = new SockJS("http://localhost:8080/chat");
            const client = Stomp.over(socket);

            client.connect({}, () => {
                console.log("‚úÖ Connected to WebSocket");

                const topic = recipient ? `/topic/private/${recipient}` : `/topic/messages`;

                client.subscribe(topic, (msg) => {
                    const receivedMessage = JSON.parse(msg.body);
                    setMessages((prev) => [
                        ...prev,
                        {
                            username: receivedMessage.username,
                            message: receivedMessage.message,
                            timestamp: receivedMessage.timestamp || new Date().toLocaleTimeString(),
                            status: receivedMessage.status
                        }
                    ]);
                });

                client.subscribe("/topic/typing", (msg) => {
                    const typingData = JSON.parse(msg.body);
                    if (typingData.username !== username) {
                        setTypingUser(typingData.username);
                        setIsTyping(true);
                        clearTimeout(typingTimeoutRef.current);
                        typingTimeoutRef.current = setTimeout(() => setIsTyping(false), 3000);
                    }
                });

                client.subscribe("/topic/active-users", (msg) => {
                    setActiveUsers(JSON.parse(msg.body));
                });

                stompClientRef.current = client;
            });
        };

        connectWebSocket();

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.disconnect();
                console.log("‚ùå Disconnected from WebSocket");
            }
        };
    }, [recipient, username]);

    const sendMessage = () => {
        if (stompClientRef.current && stompClientRef.current.connected) {
            const chatMessage = {
                username,
                message,
                recipient,
                timestamp: new Date().toLocaleTimeString(),
                status: "Sent"
            };

            stompClientRef.current.send(
                "/app/sendMessage",
                {},
                JSON.stringify(chatMessage)
            );

            setMessage("");
        }
    };

    const handleTyping = () => {
        if (stompClientRef.current && stompClientRef.current.connected) {
            stompClientRef.current.send(
                "/app/typing",
                {},
                JSON.stringify({ username })
            );
        }
    };

    // Function to get initials from username
    const getInitials = (name) => (name ? name.charAt(0).toUpperCase() : "?");

    // Function to generate a random color for each user
    const getRandomColor = (name) => {
        const colors = ["#FF5733", "#33FF57", "#3357FF", "#FF33A8", "#FF8C33"];
        let index = name ? name.charCodeAt(0) % colors.length : 0;
        return colors[index];
    };

    return (
        <div className={`chat-container ${darkMode ? 'dark-theme' : ''}`}>
            <div className="sidebar">
                <h3>Active Users</h3>
                <ul>
                    {activeUsers.length > 0 ? (
                        activeUsers.map((user, index) => (
                            <li key={index}>{user}</li>
                        ))
                    ) : (
                        <li>No active users</li>
                    )}
                </ul>
            </div>

            <div className="chat-box">
                <div className="chat-header">
                    <h2>{recipient ? `Private Chat with ${recipient}` : "Group Chat"}</h2>

                    <button className="theme-toggle-btn" onClick={() => setDarkMode(!darkMode)}>
                        {darkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
                    </button>
                </div>

                <div className="search-bar">
                    <input
                        type="text"
                        placeholder="Search messages..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                    />
                </div>

                <div className="messages">
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.username === username ? "sent" : "received"}`}>
                            {/* Profile Initials */}
                            <div
                                className="profile-initials"
                                style={{ backgroundColor: getRandomColor(msg.username) }}
                            >
                                {getInitials(msg.username)}
                            </div>

                            <div className="message-bubble">
                                <p><strong>{msg.username}</strong>: {msg.message}</p>
                                <small>{msg.timestamp} ‚Ä¢ {msg.status}</small>
                            </div>
                        </div>
                    ))}
                    {isTyping && <p className="typing-indicator">{typingUser} is typing...</p>}
                </div>

                <div className="input-container">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => {
                            setMessage(e.target.value);
                            handleTyping();
                        }}
                        placeholder="Type a message..."
                    />
                    <button onClick={sendMessage}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default ChatRoom;
