import React, { useEffect, useState, useRef } from 'react';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import EmojiPicker from 'emoji-picker-react';
import './ChatRoom.css';

const ChatRoom = ({ username, recipient }) => {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState("");
    const [activeUsers, setActiveUsers] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [darkMode, setDarkMode] = useState(false);
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const [isTyping, setIsTyping] = useState(false);

    const stompClientRef = useRef(null);
    const [isConnected, setIsConnected] = useState(false);
    const latestMessageRef = useRef(null);
    const typingTimeoutRef = useRef(null);

    useEffect(() => {
        const connectWebSocket = () => {
            const socket = new SockJS("http://localhost:8080/chat");
            const client = Stomp.over(socket);

            client.connect({}, () => {
                console.log("‚úÖ Connected to WebSocket");

                const topic = recipient ? `/topic/private/${recipient}` : `/topic/messages`;

                client.subscribe(topic, (msg) => {
                    const receivedMessage = JSON.parse(msg.body);
                    setMessages((prev) => [
                        ...prev,
                        {
                            username: receivedMessage.username,
                            message: receivedMessage.message,
                            timestamp: receivedMessage.timestamp || new Date().toLocaleTimeString(),
                            status: receivedMessage.status
                        }
                    ]);
                });

                // ‚úÖ Listen for typing events
                client.subscribe('/topic/typing', (msg) => {
                    const typingData = JSON.parse(msg.body);
                    if (typingData.username !== username) {
                        setIsTyping(true);
                        clearTimeout(typingTimeoutRef.current);
                        typingTimeoutRef.current = setTimeout(() => {
                            setIsTyping(false);
                        }, 3000); 
                    }
                });

                stompClientRef.current = client;
                setIsConnected(true);

                const intervalId = setInterval(() => {
                    fetch("http://localhost:8080/active-users")
                        .then((res) => res.json())
                        .then((data) => setActiveUsers(data))
                        .catch((error) => console.error("‚ùå Failed to fetch active users:", error));
                }, 5000);

                return () => clearInterval(intervalId);
            });
        };

        connectWebSocket();

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.disconnect();
                console.log("‚ùå Disconnected from WebSocket");
            }
        };
    }, [recipient, username]);

    useEffect(() => {
        if (latestMessageRef.current) {
            latestMessageRef.current.scrollIntoView({ behavior: "smooth" });
        }
    }, [messages]);

    const sendMessage = () => {
        if (stompClientRef.current && stompClientRef.current.connected) {
            const chatMessage = {
                username: username || "Anonymous",
                message: message || "Empty message",
                recipient: recipient || "",
                timestamp: new Date().toLocaleTimeString(),
                status: "Sent"
            };

            stompClientRef.current.send("/app/sendMessage", {}, JSON.stringify(chatMessage));
            setMessage("");
        } else {
            console.error("‚ùå WebSocket is not connected!");
            alert("Failed to send message. Connection not established.");
        }
    };

    const handleTyping = () => {
        if (stompClientRef.current && stompClientRef.current.connected) {
            stompClientRef.current.send("/app/typing", {}, JSON.stringify({ username }));
        }
    };

    const toggleTheme = () => {
        setDarkMode((prevMode) => !prevMode);
        document.body.classList.toggle('dark-page', !darkMode);
    };

    const handleEmojiClick = (emojiObject) => {
        setMessage((prev) => prev + emojiObject.emoji);
        setShowEmojiPicker(false);
    };

    // Function to get initials
    const getInitials = (name) => {
        return name ? name.charAt(0).toUpperCase() : "?";
    };

    return (
        <div className={`chat-container ${darkMode ? 'dark-theme' : ''}`}>

            <div className="sidebar">
                <h3>Active Users</h3>
                <ul>
                    {activeUsers.length > 0 ? (
                        activeUsers.map((user, index) => (
                            <li key={index}>
                                <span className="profile-initial">{getInitials(user)}</span>
                                {user}
                            </li>
                        ))
                    ) : (
                        <li>No active users</li>
                    )}
                </ul>
            </div>

            <div className="chat-box">
                <div className="chat-header">
                    <h2>{recipient ? `Private Chat with ${recipient}` : "Group Chat"}</h2>
                    <button className="theme-toggle-btn" onClick={toggleTheme}>
                        {darkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
                    </button>
                </div>

                <div className="search-bar">
                    <input
                        type="text"
                        placeholder="Search messages..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                    />
                </div>

                <div className="messages">
                    {messages.length > 0 ? (
                        messages.map((msg, index) => (
                            <div
                                key={index}
                                ref={index === messages.length - 1 ? latestMessageRef : null}
                                className={`message ${msg.username === username ? "sent" : "received"}`}
                            >
                                <span className="profile-initial">{getInitials(msg.username)}</span>
                                <div className="message-bubble">
                                    <p><strong>{msg.username}</strong>: {msg.message}</p>
                                    <small>{msg.timestamp} ‚Ä¢ {msg.status}</small>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>No messages found...</p>
                    )}
                    {/* ‚úÖ Typing Indicator */}
                    {isTyping && <p className="typing-indicator">Someone is typing...</p>}
                </div>

                <div className="input-container">
                    <button className="emoji-btn" onClick={() => setShowEmojiPicker((prev) => !prev)}>
                        üòä
                    </button>

                    {showEmojiPicker && (
                        <div className="emoji-picker-container">
                            <EmojiPicker onEmojiClick={handleEmojiClick} />
                        </div>
                    )}

                    <input
                        type="text"
                        value={message}
                        onChange={(e) => {
                            setMessage(e.target.value);
                            handleTyping();
                        }}
                        placeholder="Type a message..."
                    />
                    <button onClick={sendMessage} disabled={!isConnected}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default ChatRoom;
