import React, { useState, useEffect, useRef } from 'react';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import './ChatRoom.css';

const ChatRoom = ({ username, recipient }) => {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const stompClientRef = useRef(null);
  const latestMessageRef = useRef(null);

  useEffect(() => {
    const socket = new SockJS("http://localhost:8080/chat");
    const client = Stomp.over(socket);

    client.connect({}, () => {
      console.log("Connected!");

      const topic = recipient
        ? `/topic/private/${recipient}`
        : `/topic/messages`;

      client.subscribe(topic, (msg) => {
        const receivedMessage = JSON.parse(msg.body);
        setMessages(prev => [...prev, receivedMessage]);
      });

      stompClientRef.current = client;
    });

    return () => {
      if (stompClientRef.current) {
        stompClientRef.current.disconnect();
        console.log("Disconnected!");
      }
    };
  }, [recipient]);

  useEffect(() => {
    if (latestMessageRef.current) {
      latestMessageRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [messages]);

  const sendMessage = () => {
    if (!message.trim()) return; // don't send empty messages

    if (stompClientRef.current && stompClientRef.current.connected) {
      const chatMessage = {
        username: username || "Anonymous",
        message,
        recipient: recipient || "",
        timestamp: new Date().toLocaleTimeString(),
      };
      stompClientRef.current.send("/app/sendMessage", {}, JSON.stringify(chatMessage));
      setMessage("");
    } else {
      alert("Not connected.");
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-box">
        <h2>{recipient ? `Chat with ${recipient}` : "Group Chat"}</h2>

        <div className="messages" style={{ height: "400px", overflowY: "auto", border: "1px solid #ccc", padding: "10px" }}>
          {messages.map((msg, i) => (
            <div
              key={i}
              ref={i === messages.length - 1 ? latestMessageRef : null}
              style={{ marginBottom: "10px", textAlign: msg.username === username ? "right" : "left" }}
            >
              <b>{msg.username}:</b> {msg.message} <br />
              <small>{msg.timestamp}</small>
            </div>
          ))}
        </div>

        <div style={{ marginTop: "10px" }}>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Type your message"
            style={{ width: "80%", padding: "8px" }}
            onKeyDown={e => e.key === 'Enter' && sendMessage()}
          />
          <button onClick={sendMessage} style={{ padding: "8px 12px", marginLeft: "8px" }}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default ChatRoom;
