import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ChatDashboard.css';

const ChatDashboard = () => {
    const [username, setUsername] = useState('');
    const [recipient, setRecipient] = useState('');
    const navigate = useNavigate();

    return (
        <div className="dashboard-container">
            <h1>ðŸ”¥ Chat Options</h1>
            <input type="text" placeholder="Enter Username" value={username} onChange={(e) => setUsername(e.target.value)} />
            <button onClick={() => navigate(`/group/${username}`)}>Join Group Chat</button>
            <input type="text" placeholder="Recipient" value={recipient} onChange={(e) => setRecipient(e.target.value)} />
            <button onClick={() => navigate(`/private/${username}/${recipient}`)}>Start Private Chat</button>
        </div>
    );
};

export default ChatDashboard;
