import React, { useState, useEffect } from "react";
import { Client } from "@stomp/stompjs";
import Message from "./Message";
import "./ChatRoom.css";

let stompClient = null;
const MAX_RECONNECT_ATTEMPTS = 5;
let reconnectAttempts = 0;

const ChatRoom = ({ username }) => {
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState("");

    useEffect(() => {
        const connectWebSocket = () => {
            if (reconnectAttempts >= MAX_RECONNECT_ATTEMPTS) {
                console.error("Max reconnection attempts reached.");
                return;
            }

            stompClient = new Client({
                brokerURL: "ws://localhost:8080/chat",  // ✅ WebSocket endpoint
                reconnectDelay: 5000,                   // ✅ Retry delay
                heartbeatIncoming: 4000,
                heartbeatOutgoing: 4000,
                debug: (str) => console.log(str)        // ✅ Log errors
            });

            stompClient.onConnect = () => {
                console.log("Connected to WebSocket");
                reconnectAttempts = 0;

                stompClient.subscribe("/topic/messages", (message) => {
                    const receivedMessage = JSON.parse(message.body);
                    setMessages((prevMessages) => [...prevMessages, receivedMessage]);
                });
            };

            stompClient.onWebSocketError = (error) => {
                console.error("WebSocket Error:", error);
                reconnectAttempts++;
            };

            stompClient.activate();
        };

        connectWebSocket();

        return () => {
            if (stompClient) {
                stompClient.deactivate();
            }
        };
    }, []);

    const sendMessage = () => {
        if (stompClient && stompClient.connected && newMessage.trim()) {
            const message = {
                sender: username,
                content: newMessage
            };

            stompClient.publish({
                destination: "/app/chat",
                body: JSON.stringify(message)
            });

            setNewMessage("");
        } else {
            console.error("STOMP connection not available.");
        }
    };

    return (
        <div className="chat-room">
            <h2>Welcome, {username}!</h2>
            <div className="messages-container">
                {messages.map((msg, index) => (
                    <Message key={index} sender={msg.sender} content={msg.content} />
                ))}
            </div>
            <div className="input-container">
                <input
                    type="text"
                    placeholder="Type a message..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                />
                <button onClick={sendMessage}>Send</button>
            </div>
        </div>
    );
};

export default ChatRoom;
