import React, { useEffect, useState } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { useNavigate } from "react-router-dom";
import "./ChatRoom.css";  // Importing the CSS file

const ChatRoom = ({ username }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  const client = new Client({
    webSocketFactory: () => new SockJS("http://localhost:8080/chat"),
  });

  useEffect(() => {
    client.onConnect = () => {
      console.log("Connected to WebSocket");

      // Subscribe to group chat
      client.subscribe("/topic/group", (message) => {
        const msg = JSON.parse(message.body);
        setMessages((prev) => [...prev, msg]);
      });

      // Subscribe to active users list
      client.subscribe("/topic/users", (message) => {
        const userList = JSON.parse(message.body);
        setUsers(userList);
      });

      client.publish({
        destination: "/app/join",
        body: username,
      });
    };

    client.activate();
    return () => client.deactivate();
  }, [username]);

  const sendMessage = () => {
    if (input.trim()) {
      client.publish({
        destination: "/app/send",
        body: JSON.stringify({ username, content: input }),
      });
      setInput("");
    }
  };

  return (
    <div className="chat-container">

      {/* Sidebar for Users */}
      <div className="sidebar">
        <h3>Active Users:</h3>
        <ul>
          {users.map((user, index) => (
            <li key={index} className="user-item">
              <span>{user}</span>
              {user !== username && (
                <button
                  className="private-btn"
                  onClick={() => navigate(`/private/${user}`)}
                >
                  Private Chat
                </button>
              )}
            </li>
          ))}
        </ul>
      </div>

      {/* Group Chat Section */}
      <div className="chat-section">
        <h2>Group Chat</h2>
        <div className="message-container">
          {messages.map((msg, index) => (
            <div key={index} className="message">
              <strong>{msg.username}:</strong> {msg.content}
            </div>
          ))}
        </div>

        <div className="input-container">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type a message..."
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default ChatRoom;
