import React, { useState, useEffect } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { useLocation } from "react-router-dom";
import "./ChatRoom.css";

const ChatRoom = () => {
  const [client, setClient] = useState(null);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");

  const location = useLocation();
  const username = location.state?.username || "Guest";

  useEffect(() => {
    const socket = new SockJS("http://localhost:8080/ws");

    const stompClient = new Client({
      webSocketFactory: () => socket,
      reconnectDelay: 5000,
      onConnect: () => {
        console.log("Connected to WebSocket");

        stompClient.subscribe("/topic/messages", (msg) => {
          const receivedMsg = JSON.parse(msg.body);
          setMessages((prev) => [...prev, receivedMsg]);
        });

        fetch("http://localhost:8080/api/chat/history")
          .then((res) => res.json())
          .then((data) => setMessages(data));
      },
      onStompError: (frame) => console.error("Broker error:", frame),
      onWebSocketClose: () => console.log("WebSocket closed. Reconnecting..."),
    });

    stompClient.activate();
    setClient(stompClient);

    return () => stompClient.deactivate();
  }, []);

  const sendMessage = () => {
    if (client && client.connected && message.trim()) {
      client.publish({
        destination: "/app/chat",
        body: JSON.stringify({ sender: username, content: message }),
      });
      setMessage("");
    }
  };

  return (
    <div className="chat-room">
      <div className="messages">
        {messages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.sender}:</strong> {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default ChatRoom;
