import React, { useState } from 'react';
import GroupChat from './GroupChat';
import PrivateChat from './PrivateChat';
import './UsernamePage.css';

const UsernamePage = () => {
    const [username, setUsername] = useState("");
    const [chatType, setChatType] = useState("");
    const [profilePic, setProfilePic] = useState(null);

    const handleProfilePicChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = () => {
                setProfilePic(reader.result); // Store base64 image data
            };
            reader.readAsDataURL(file);
        }
    };

    const enterChat = (type) => {
        setChatType(type);
    };

    return (
        <div className="username-container">
            {!chatType ? (
                <div className="username-box">
                    <h2>Welcome to Real-Time Chat!</h2>

                    {/* Profile Picture Upload */}
                    <label className="profile-pic-label">
                        <input type="file" accept="image/*" onChange={handleProfilePicChange} hidden />
                        <div className="profile-pic-preview">
                            {profilePic ? <img src={profilePic} alt="Profile" /> : <span>ðŸ“· Upload Photo</span>}
                        </div>
                    </label>

                    {/* Username Input */}
                    <input
                        type="text"
                        placeholder="Enter your username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        className="username-input"
                    />

                    {/* Chat Options */}
                    <div className="button-group">
                        <button onClick={() => enterChat("group")} className="chat-btn">Group Chat</button>
                        <button onClick={() => enterChat("private")} className="chat-btn">Private Chat</button>
                    </div>
                </div>
            ) : (
                chatType === "group" ? (
                    <GroupChat username={username} profilePic={profilePic} />
                ) : (
                    <PrivateChat username={username} profilePic={profilePic} />
                )
            )}
        </div>
    );
};

export default UsernamePage;
