import React, { useState, useEffect } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import "./ChatRoom.css";

const ChatRoom = () => {
  const [client, setClient] = useState(null);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const socket = new SockJS("http://localhost:8080/ws");
    const stompClient = new Client({
      webSocketFactory: () => socket,
      reconnectDelay: 5000,   // ✅ Prevents frequent reconnects
      onConnect: () => {
        console.log("Connected to WebSocket");
        
        // ✅ Subscribe to topic
        stompClient.subscribe("/topic/messages", (msg) => {
          console.log("Received message:", msg.body);
          const receivedMsg = JSON.parse(msg.body);

          // ✅ Append the new message to the state
          setMessages((prev) => [...prev, receivedMsg]);
        });

        // ✅ Fetch existing messages from the backend
        fetch("http://localhost:8080/api/chat/history")
          .then((res) => res.json())
          .then((data) => {
            console.log("Fetched messages:", data);
            setMessages(data);  // ✅ Display old messages
          });
      },
      onStompError: (frame) => {
        console.error("Broker error:", frame);
      },
      onWebSocketClose: () => {
        console.log("WebSocket closed. Reconnecting...");
      },
    });

    stompClient.activate();
    setClient(stompClient);

    return () => {
      if (stompClient) stompClient.deactivate();
    };
  }, []);

  const sendMessage = () => {
    if (client && client.connected && message.trim() !== "") {
      client.publish({
        destination: "/app/chat",
        body: JSON.stringify({ sender: "User", content: message }),
      });
      setMessage("");
    }
  };

  return (
    <div className="chat-room">
      <div className="messages">
        {messages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.sender}:</strong> {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default ChatRoom;
