import React, { useEffect, useState } from 'react';
import { connectWebSocket, sendMessage } from './WebSocketService';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');

  useEffect(() => {
    connectWebSocket((message) => {
      setMessages((prev) => [...prev, message]);
    });
  }, []);

  const handleSendMessage = () => {
    if (inputMessage.trim() !== '') {
      sendMessage({ sender: 'User', content: inputMessage }); // Dynamic message sending
      setInputMessage('');
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <h1>Real-Time Chat App</h1>

      <div style={{ border: '1px solid black', padding: '10px', minHeight: '300px', overflowY: 'auto' }}>
        {messages.map((msg, index) => (
          <div key={index}><strong>{msg.sender}:</strong> {msg.content}</div>
        ))}
      </div>

      <div style={{ marginTop: '10px' }}>
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          placeholder="Type your message..."
          style={{ width: '80%', padding: '10px' }}
        />
        <button onClick={handleSendMessage} style={{ padding: '10px 20px' }}>
          Send
        </button>
      </div>
    </div>
  );
};

export default App;
