import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import JoinRoom from "./components/JoinRoom";
import ChatDashboard from "./components/ChatDashboard";
import GroupChat from "./components/GroupChat";
import PrivateChat from "./components/PrivateChat";

function App() {
  const [username, setUsername] = useState("");

  return (
    <Router>
      <Routes>
        <Route path="/" element={<JoinRoom setUsername={setUsername} />} />
        <Route path="/dashboard" element={<ChatDashboard username={username} />} />
        <Route path="/group" element={<GroupChat username={username} />} />
        <Route path="/private/:recipient" element={<PrivateChat username={username} />} />
      </Routes>
    </Router>
  );
}

export default App;
