import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Client } from '@stomp/stompjs';
import './PrivateChat.css';  // ‚úÖ Add CSS styling

const PrivateChat = () => {
    const { sender, recipient } = useParams();
    const [client, setClient] = useState(null);
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const stompClient = new Client({
            brokerURL: 'ws://localhost:8080/ws',
            reconnectDelay: 5000,
            onConnect: () => {
                console.log('Connected to Private Chat');

                // ‚úÖ Subscribe to private messages
                stompClient.subscribe(`/user/${sender}/queue/messages`, (msg) => {
                    const newMessage = JSON.parse(msg.body);
                    setMessages((prev) => [...prev, newMessage]);
                });

                // ‚úÖ Notify recipient
                stompClient.publish({
                    destination: '/app/private',
                    body: JSON.stringify({ sender, recipient, content: `${sender} joined the chat!` }),
                });
            },
            onDisconnect: () => console.log('Disconnected from Private Chat')
        });

        stompClient.activate();
        setClient(stompClient);

        return () => {
            stompClient.deactivate();
        };
    }, [sender, recipient]);

    const sendMessage = () => {
        if (client && message.trim() !== '') {
            const msg = {
                sender,
                recipient,
                content: message,
                timestamp: new Date().toISOString()
            };

            client.publish({
                destination: '/app/private',
                body: JSON.stringify(msg),
            });

            setMessage('');
        }
    };

    return (
        <div className="chat-container">
            <button onClick={() => navigate('/')} className="btn-back">‚¨ÖÔ∏è Back</button>
            
            <h1>üî• Private Chat with {recipient}</h1>

            <div className="messages-container">
                {messages.map((msg, index) => (
                    <div key={index} className="message">
                        <strong>{msg.sender}:</strong> {msg.content}
                        <span className="timestamp">{new Date(msg.timestamp).toLocaleTimeString()}</span>
                    </div>
                ))}
            </div>

            <div className="input-container">
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type a message..."
                />
                <button onClick={sendMessage}>Send</button>
            </div>
        </div>
    );
};

export default PrivateChat;
