import React, { useEffect, useState } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { useNavigate } from "react-router-dom";

const ChatRoom = ({ username }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  const client = new Client({
    webSocketFactory: () => new SockJS("http://localhost:8080/chat"),
  });

  useEffect(() => {
    client.onConnect = () => {
      console.log("Connected to WebSocket");

      // Subscribe to group chat
      client.subscribe("/topic/group", (message) => {
        const msg = JSON.parse(message.body);
        setMessages((prev) => [...prev, msg]);
      });

      // Subscribe to active users list
      client.subscribe("/topic/users", (message) => {
        const userList = JSON.parse(message.body);
        setUsers(userList);
      });

      client.publish({
        destination: "/app/join",
        body: username,
      });
    };

    client.activate();
    return () => client.deactivate();
  }, [username]);

  const sendMessage = () => {
    if (input.trim()) {
      client.publish({
        destination: "/app/send",
        body: JSON.stringify({ username, content: input }),
      });
      setInput("");
    }
  };

  return (
    <div>
      <h2>Group Chat</h2>

      <div>
        <h3>Active Users:</h3>
        <ul>
          {users.map((user, index) => (
            <li key={index}>
              {user}{" "}
              <button onClick={() => navigate(`/private/${user}`)}>
                Private Chat
              </button>
            </li>
          ))}
        </ul>
      </div>

      <div>
        {messages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.username}:</strong> {msg.content}
          </div>
        ))}
      </div>

      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default ChatRoom;
