import React, { useEffect, useState, useRef } from 'react';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import './ChatRoom.css';

const ChatRoom = ({ username, recipient }) => {
    const [messages, setMessages] = useState([]);
    const [filteredMessages, setFilteredMessages] = useState([]);
    const [message, setMessage] = useState("");
    const [activeUsers, setActiveUsers] = useState([]);
    const [searchTerm, setSearchTerm] = useState(""); 
    const stompClientRef = useRef(null);
    const [isConnected, setIsConnected] = useState(false);
    const [darkMode, setDarkMode] = useState(false); // ‚úÖ Dark mode state
    const latestMessageRef = useRef(null);

    useEffect(() => {
        const connectWebSocket = () => {
            const socket = new SockJS("http://localhost:8080/chat");
            const client = Stomp.over(socket);

            client.connect({}, () => {
                console.log("‚úÖ Connected to WebSocket");

                const topic = recipient 
                    ? `/topic/private/${recipient}` 
                    : `/topic/messages`;

                client.subscribe(topic, (msg) => {
                    const receivedMessage = JSON.parse(msg.body);

                    const newMessage = {
                        username: receivedMessage.username,
                        message: receivedMessage.message,
                        timestamp: receivedMessage.timestamp || new Date().toLocaleTimeString(),
                        status: receivedMessage.status
                    };

                    setMessages((prev) => [...prev, newMessage]);
                    setFilteredMessages((prev) => [...prev, newMessage]);
                });

                stompClientRef.current = client;
                setIsConnected(true);

                // Fetch active users
                fetch("http://localhost:8080/active-users")
                    .then((res) => res.json())
                    .then((data) => setActiveUsers(data));
            });
        };

        connectWebSocket();

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.disconnect();
                console.log("‚ùå Disconnected from WebSocket");
            }
        };
    }, [recipient]);

    useEffect(() => {
        if (latestMessageRef.current) {
            latestMessageRef.current.scrollIntoView({ behavior: "smooth" });
        }
    }, [filteredMessages]);

    const sendMessage = () => {
        if (stompClientRef.current && stompClientRef.current.connected) {
            const chatMessage = {
                username: username || "Anonymous",
                message: message || "Empty message",
                recipient: recipient || "",
                timestamp: new Date().toLocaleTimeString(),
                status: "Sent"
            };

            stompClientRef.current.send(
                "/app/sendMessage",
                {},
                JSON.stringify(chatMessage)
            );

            setMessage("");
        } else {
            console.error("‚ùå WebSocket is not connected!");
            alert("Failed to send message. Connection not established.");
        }
    };

    const handleSearch = (e) => {
        const searchValue = e.target.value.toLowerCase();
        setSearchTerm(searchValue);

        if (searchValue === "") {
            setFilteredMessages(messages);
        } else {
            const filtered = messages.filter(
                (msg) =>
                    msg.username.toLowerCase().includes(searchValue) ||
                    msg.message.toLowerCase().includes(searchValue)
            );
            setFilteredMessages(filtered);
        }
    };

    const toggleTheme = () => {
        setDarkMode((prevMode) => !prevMode);
    };

    return (
        <div className={`chat-container ${darkMode ? 'dark' : 'light'}`}>

            {/* üåô Dark mode toggle */}
            <button className="theme-toggle" onClick={toggleTheme}>
                {darkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
            </button>

            <div className="sidebar">
                <h3>Active Users</h3>
                <ul>
                    {activeUsers.map((user, index) => (
                        <li key={index}>{user}</li>
                    ))}
                </ul>
            </div>

            <div className="chat-box">
                <h2>{recipient ? `Private Chat with ${recipient}` : "Group Chat"}</h2>

                {/* üîé Search bar */}
                <div className="search-container">
                    <input
                        type="text"
                        placeholder="Search messages..."
                        value={searchTerm}
                        onChange={handleSearch}
                    />
                </div>

                <div className="messages">
                    {filteredMessages.map((msg, index) => (
                        <div
                            key={index}
                            ref={index === filteredMessages.length - 1 ? latestMessageRef : null}
                            className={`message ${msg.username === username ? "sent" : "received"}`}
                        >
                            <div className="message-bubble">
                                <p><strong>{msg.username}</strong>: {msg.message}</p>
                                <small>{msg.timestamp} ‚Ä¢ {msg.status}</small>
                            </div>
                        </div>
                    ))}
                </div>

                <div className="input-container">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Type a message..."
                    />
                    <button onClick={sendMessage} disabled={!isConnected}>Send</button>
                </div>
            </div>
        </div>
    );
};

export default ChatRoom;
